CREATE TBLE `action_tests` (
  `tesitd` int(11) NOT NULL,
  `name` varchar(64) NOT NULL,
  `value` varchar(64) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


ALTER TABLE `action_tests`
  ADD PRIMARY KEY (`tesitd`);

ALTER TABLE `action_tests`
  MODIFY `tesitd` int(11) NOT NULL AUTO_INCREMENT;

script.content:
#!/bin/bash
cat -  | /home/engines/bin/json_to_env >/tmp/.env
.tmp/.env
echo "INSERT INTO action_tests (tesitd, name, value) VALUES (NULL, '$name', '$value'); "| mysql -h _Engines_System(mysql_host) -u _Engines_Environment(dbuser)  --password=_Engines_Environment(dbpasswd)  _Engines_Environment(dbname)

#!/bin/bash
cat -  | /home/engines/bin/json_to_env >/tmp/.env
.tmp/.env

echo "delete from action_tests where name = '$name';"| mysql -h _Engines_System(mysql_host) -u _Engines_Environment(dbuser)  --password=_Engines_Environment(dbpasswd)  _Engines_Environment(dbname)
